import { useEffect, useState } from "react";
import { fetchVehicles, deleteVehicle } from "../api";

export default function VehicleList({ onEdit }) {
  const [vehicles, setVehicles] = useState([]);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  const loadVehicles = async () => {
    try {
      const data = await fetchVehicles();
      setVehicles(data);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    loadVehicles();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this vehicle?")) return;
    try {
      await deleteVehicle(id);
      loadVehicles();
    } catch (err) {
      alert(err.message);
    }
  };

  const filteredVehicles = vehicles.filter(
    (v) =>
      v.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      v.license_plate.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <input
        type="text"
        placeholder="Search vehicles..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: "1rem", padding: "0.5rem", width: "100%" }}
      />
      {filteredVehicles.length === 0 ? (
        <p>No vehicles found.</p>
      ) : (
        <ul>
          {filteredVehicles.map((v) => (
            <li key={v.id} style={{ marginBottom: "0.5rem" }}>
              <strong>{v.name}</strong> - {v.license_plate}{" "}
              <button onClick={() => onEdit(v)} style={{ marginLeft: "1rem" }}>
                Edit
              </button>{" "}
              <button onClick={() => handleDelete(v.id)} style={{ color: "red" }}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
