import { useState, useEffect } from "react";
import { createVehicle, updateVehicle } from "../api";

export default function VehicleForm({ onSuccess, editingVehicle, onCancel }) {
  const [form, setForm] = useState({ name: "", license_plate: "" });
  const [error, setError] = useState(null);

  useEffect(() => {
    if (editingVehicle) {
      setForm({
        name: editingVehicle.name,
        license_plate: editingVehicle.license_plate,
      });
    } else {
      setForm({ name: "", license_plate: "" });
    }
  }, [editingVehicle]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      if (editingVehicle) {
        await updateVehicle(editingVehicle.id, form);
      } else {
        await createVehicle(form);
      }
      setForm({ name: "", license_plate: "" });
      onSuccess();
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "1rem" }}>
      <input
        name="name"
        placeholder="Name"
        value={form.name}
        onChange={handleChange}
        required
      />
      <input
        name="license_plate"
        placeholder="License Plate"
        value={form.license_plate}
        onChange={handleChange}
        required
      />
      <button type="submit">{editingVehicle ? "Update" : "Add"} Vehicle</button>
      {editingVehicle && (
        <button type="button" onClick={onCancel} style={{ marginLeft: "1rem" }}>
          Cancel
        </button>
      )}
      {error && <div style={{ color: "red" }}>{error}</div>}
    </form>
  );
}
